#!/bin/bash                                                                                                             echo "calculadora simples"                                                                                               echo "primeiro numero: "                                                                                                read num1                                                                                                               echo "segundo numero: "                                                                                                 read num2                                                                                                               echo "qual operaçâo (+, -, *, /): "                                                                                     read op                                                                                                                                                                                                                                         case $op in                                                                                                                 +) resultado=$(echo "$num1 + $num2" | bc) ;;                                                                            -) resultado=$(echo "$num1 - $num2" | bc) ;;                                                                            \*) resultado=$(echo "$num1 * $num2" | bc) ;;                                                                           /) resultado=$(echo "scale=2; $num1 / $num2" | bc) ;;                                                                   *) echo "Operaçâo invalida"; exit 1 ;;                                                                              esac                                                                                                                                                                                                                                            echo " resultado é: $resultado"